mapper.select:
select member_id, email, password, name, phone, address, member_type, created_at, updated_at
from member
where email = #{email}

select member_id, email, name, phone, address, member_type, created_at
from member
where member_id = #{memberId}

mapper.update:
update member
set
name = #{member.name},
phone = #{member.phone},
address = #{member.address}
where member_id = #{member.memberId}
